INSERT INTO `table_feedback` VALUES("1","Nguyễn Thanh Tùng","","Dịch Vụ Quản Lý Tòa Nhà Tuyệt Vời","Tôi đã sống tại nhiều tòa nhà chung cư khác nhau nhưng chưa bao giờ gặp một dịch vụ quản lý và vận hành tốt như ở đây. Nhân viên rất tận tâm, luôn sẵn sàng hỗ trợ và giải quyết mọi vấn đề một cách nhanh chóng và hiệu quả.","storage/images/testimonial/1725873842.jpg","4","1","1");
INSERT INTO `table_feedback` VALUES("2","Lê Minh Anh","","Dịch Vụ Giữ Xe 24/24 An Toàn và Tiện Lợi","Hệ thống giữ xe 24/24 thật sự làm tôi cảm thấy yên tâm. An ninh luôn được đảm bảo, xe của tôi luôn an toàn và dễ dàng ra vào bất cứ lúc nào. Đội ngũ nhân viên rất thân thiện và chu đáo.","storage/images/testimonial/1725873887.jpg","5","2","1");
INSERT INTO `table_feedback` VALUES("3","Trần Hoàng Nam","","An Ninh Tuyệt Đối","Dịch vụ an ninh ở đây thật sự xuất sắc. Hệ thống camera giám sát và nhân viên bảo vệ làm việc 24/7 giúp tôi cảm thấy rất an toàn. Họ luôn kiểm tra và bảo vệ rất nghiêm ngặt.","storage/images/testimonial/1725873895.jpg","4","3","1");
INSERT INTO `table_feedback` VALUES("4","Phạm Thị Hương","","Dịch Vụ Vận Chuyển Nhanh và An Toàn","Tôi đã sử dụng dịch vụ vận chuyển nhiều lần và luôn hài lòng với sự nhanh chóng và chuyên nghiệp của đội ngũ nhân viên. Hàng hóa của tôi luôn được vận chuyển cẩn thận và an toàn.","storage/images/testimonial/1725873904.jpg","5","4","1");
INSERT INTO `table_feedback` VALUES("5","Nguyễn Văn Hải","","Chất Lượng Dịch Vụ Điện Lạnh Xuất Sắc","Dịch vụ điện lạnh rất chuyên nghiệp và hiệu quả. Nhân viên sửa chữa nhanh chóng, kiểm tra và bảo dưỡng thiết bị một cách kỹ lưỡng. Tôi rất hài lòng với dịch vụ này.","storage/images/testimonial/1725873912.jpg","5","5","1");
INSERT INTO `table_feedback` VALUES("6","Trần Thị Lan","","Dịch Vụ Vệ Sinh Chuyên Nghiệp","Tôi rất hài lòng với dịch vụ vệ sinh chuyên nghiệp tại đây. Khu vực công cộng và căn hộ của tôi luôn được giữ sạch sẽ và thoáng mát. Nhân viên vệ sinh làm việc rất cẩn thận và chu đáo.","storage/images/testimonial/1725873920.jpg","5","6","1");
INSERT INTO `table_feedback` VALUES("7","Hoàng Anh Tú","","Dịch Vụ Giặt Ủi Tuyệt Vời","Dịch vụ giặt ủi ở đây thật sự tiện lợi và chất lượng. Quần áo của tôi luôn được giặt sạch và ủi phẳng phiu, giao nhận nhanh chóng và đúng hẹn. Tôi rất hài lòng và sẽ tiếp tục sử dụng dịch vụ.","storage/images/testimonial/1725873930.jpg","5","7","1");



