INSERT INTO `table_static` (`id`, `ten_vi`, `photo`, `video`, `mota_vi`, `noidung_vi`, `type`, `sl_nhanvien`, `sl_duan`, `sl_canho`, `tamnhin`, `sumenh`, `sl_khachhang`, `sl_giaithuong`, `sl_doitac`, `mota_solieu`, `mota_doitac`) VALUES
(1, 'Câu chuyện thành công', 'upload/vechungtoi/87088154.png', 'https://www.youtube.com/watch?v=BwUaGq-nQ9Q&themeRefresh=1', '', '<p>Được th&agrave;nh lập ng&agrave;y 06 th&aacute;ng 09 năm 2010, C&ocirc;ng ty TNHH MTV Dịch Vụ Quản L&yacute; Cao Ốc Kh&aacute;nh Hội - KHASERVICE được th&agrave;nh lập với 100% vốn chủ sở hữu của c&ocirc;ng ty Cổ Phần Đầu Tư v&agrave; Dịch Vụ Kh&aacute;nh Hội - KHAHOMEX hoạt động trong lĩnh vực: Quản l&yacute; vận h&agrave;nh cao ốc c&ugrave;ng với những dịch vụ kh&aacute;c như giữ xe thẻ th&ocirc;ng minh, dịch vụ bảo tr&igrave; sửa chữa điện, nước, điện lạnh, vệ sinh, giặt ủi...</p>\r\n\r\n<p>V&agrave;o đầu th&aacute;ng 01/2018, C&ocirc;ng ty đ&atilde; đổi t&ecirc;n th&agrave;nh&nbsp;C&ocirc;ng ty Cổ Phần Quản L&yacute; V&agrave; Vận H&agrave;nh Cao Ốc Kh&aacute;nh Hội - KhaServices.&nbsp;KhaServices đ&atilde; v&agrave; đang&nbsp;lớn mạnh,&nbsp;ph&aacute;t triển kh&ocirc;ng ngừng, sẽ lu&ocirc;n đồng h&agrave;nh c&ugrave;ng Qu&yacute; kh&aacute;ch h&agrave;ng v&agrave; tạo n&ecirc;n những dịch vụ tốt nhất, chất lượng nhất.</p>\r\n', 've-chung-toi', 800, 37, 10000, '<p>Trở th&agrave;nh một doanh nghiệp dịch vụ Bất động sản vững mạnh,&nbsp;kh&ocirc;ng ngừng ph&aacute;t triển về chất lượng&nbsp;v&agrave; chuy&ecirc;n nghiệp.</p>\r\n', '<p>C&ugrave;ng với sức trẻ, t&acirc;m huyết, sự năng động v&agrave; s&aacute;ng tạo KhaServices&nbsp;sẽ gi&uacute;p c&aacute;c kh&aacute;ch h&agrave;ng lựa chọn được những dịch vụ ph&ugrave; hợp với nhu cầu v&agrave; triển khai quản l&yacute; điều h&agrave;nh với phương thức chuy&ecirc;n nghiệp nhất, mang đến hiệu quả v&agrave; chất lượng cao nhất.</p>\r\n\r\n<p><span style=\"color:#27ae60\"><strong><em>&quot;Dịch vụ tốt nhất, tiện lợi nhất v&agrave; mang đến sự h&agrave;i l&ograve;ng nhất cho Kh&aacute;ch h&agrave;ng&quot;.</em></strong></span></p>\r\n', 1000, 20, 50, '<h1><span style=\"color:#1abc9c\"><strong>Hơn 10 Năm</strong>&nbsp;</span><span style=\"color:#dddddd\">kinh nghiệm</span></h1>\r\n\r\n<h1><span style=\"color:#dddddd\">trong lĩnh vực&nbsp;</span></h1>\r\n\r\n<h1><span style=\"color:#1abc9c\"><strong>Quản l&yacute; vận h&agrave;nh chung cư</strong></span></h1>\r\n', '<h1>Hơn 50+ đối t&aacute;c tin tưởng ch&uacute;ng t&ocirc;i</h1>\r\n\r\n<p style=\"text-align:justify\">Trong thời gian ngắn, hơn h&agrave;ng trăm đối t&aacute;c kh&aacute;ch h&agrave;ng đ&atilde; tin tưởng ch&uacute;ng t&ocirc;i nỗ lực hết m&igrave;nh đồng h&agrave;nh c&ugrave;ng c&aacute;c đối t&aacute;c quản l&yacute; vận h&agrave;nh chung cư, cung cấp một nền tảng chuyển đổi số tiện lợi, hiện đại mang lại cho d&acirc;n cư những trải nghiệm tuyệt vời.</p>\r\n');

